INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('5', '2', '公司简介', '<!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility><w:DontGrowAutofit /><w:BalanceSingleByteDoubleByteWidth /><w:DoNotExpandShiftReturn /><w:UseFELayout /></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]-->\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:12.0000pt; font-family:\'宋体\'; \">公司简介</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">杭州笨虎网络科技有限公司位于美丽的西子湖畔----浙江杭州。注册资金1000万人民币。坐落于杭州城西---文二西路一号元茂大厦。</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">是一家主要从事自主网站开发、软件研发的公司。</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">公司旗下主推网站---&ldquo;笨虎之家&rdquo;</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:12.0000pt; font-family:\'宋体\'; \">发展历史</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">2009年嵊州梦想网络有限公司在浙江嵊州成立，本着想实现软件领域的一些创新想法，于是取名梦想网络。</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">梦想越来越让人觉得奢侈，可没有梦想的人生是苍白的。梦想网络集结了一批年轻又有想法的软件人才开始了他们的梦想之路。4年多的时间，已有不小成就。2013年，为了扩大发展，梦想网络在杭州成立了分公司----杭州笨虎网络科技有限公司。过去的成就已经过去，我们要的是未来。我们不激进，踏踏实实走好每一步----&ldquo;笨&rdquo;，我们有活力，一路勇往直前---&ldquo;虎&rdquo;。</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:12.0000pt; font-family:\'宋体\'; \">公司战略</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">笨虎</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">的追求是在</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">互联网</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">领域实现</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">客户和笨虎人</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">的梦想，并依靠点点滴滴、锲而不舍的艰苦追求，使我们成为</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">互联网行业的</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">领先企业。&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:12.0000pt; font-family:\'宋体\'; \">企业文化</span></p>\r\n<p style=\"text-indent:24.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&ldquo;笨虎人&rdquo;是一群有活力有创意的80、90后，非常年轻的一支团队，我们有&ldquo;梦想人&rdquo;的敢想敢干，勇于创新；有&ldquo;笨虎人&rdquo;的脚踏实地，高瞻远瞩。</span></p>\r\n<p style=\"text-indent:21.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:10.5000pt; font-family:\'宋体\'; \">这里有独特的笨虎文化：</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:left; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">我们永远在学习，现代社会，&ldquo;唯一不变的是变化&rdquo;，不进则退。</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:left; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">我们永远在创新，对一切未知的好奇，发现它，探索它并创造它。</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:left; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">我们永远在协作，一个人的力量终归有限，&ldquo;三个臭皮匠，顶个诸葛亮&rdquo;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:left; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">我们永远在体验，站在客户的角度让产品更亲近、更完美。</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:21.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:10.5000pt; font-family:\'宋体\'; \">这里有独特的笨虎氛围：</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">自由是它的宗旨</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">让你的思维自由，不设限，不设框</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">让你的言论自由，表达你内心最真实的声音。</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:21.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:10.5000pt; font-family:\'宋体\'; \">这里有独特的笨虎价值观：</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">追求</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">:笨虎</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">的追求是在</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">互联网</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">领域实现</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">客户</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">的梦想，并依靠点点滴滴、锲而不舍的艰苦追求，使我们成为</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">互联网行业的</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">领先企业。&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">员工</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">:</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">认真负责和管理有效的员工是</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">笨虎</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">最大的财富。尊重知识、尊重个性、集体奋斗和不迁就有功的员工，是我们事业可持续成长的内在要求。&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">创新：</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">广泛吸收世界</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">互联网</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">领域的最新</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">动态</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">，虚心向国内外优秀企业学习，在独立自主的基础上，</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">运用创新理念和技能发展公司各类产品，达到日趋完美的状态。</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">精神</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">：</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">爱事业和爱生活是我们凝聚力的源泉。责任意识、</span><span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">敬业精神</span></span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">与团结合作精神是我们企业文化的精髓。</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">共赢：笨虎</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">主张在客</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">户、</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">员工与</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">公司</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">之间结成利益共同体。努力探索</span><span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">按生产要素分配</span></span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">的内部动力机制。我们决不让雷锋吃亏，奉献者定当得到合理的回报。</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">以及员工个人的职业发展前景。</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;&nbsp;&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:30.0000pt; margin-bottom:0pt; margin-top:0pt; text-autospace:ideograph-numeric; text-autospace:ideograph-other; text-align:justify; vertical-align:; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">文化</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">：</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">资源是会枯竭的，唯有文化才会生生不息。一切工业产品都是人类智慧创造的。</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">笨虎</span><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">没有可以依存的自然资源，唯有在人的头脑中挖掘出大油田、大森林、大煤矿&hellip;&hellip;。</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"margin-bottom:0pt; margin-top:0pt; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:normal; font-size:12.0000pt; font-family:\'宋体\'; \">&nbsp;</span></p>\r\n<p style=\"text-indent:28.0000pt; margin-bottom:0pt; margin-top:0pt; text-align:center; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:14.0000pt; font-family:\'宋体\'; \">对于用户：用户的需求是我们永远的追求目标。</span></p>\r\n<p style=\"text-indent:28.0000pt; margin-bottom:0pt; margin-top:0pt; text-align:center; \" class=\"p0\"><span style=\"mso-spacerun:\'yes\'; font-weight:bold; font-size:14.0000pt; font-family:\'宋体\'; \">对于员工：一个真正的好员工，是会工作会生活的人。</span></p>\r\n<p style=\"