INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('9', '5', '常见问题', '<p><span style=\"font-size: medium;\">1、笨虎之家总是无法链接，这是怎么回事？<br />\r\n</span><span style=\"font-size: medium;\">请您先刷新一下；或者检查一下网络是否正常，能否登录其它网站，如果以上两种方式都无效，还有一种情况是网页正在更新，可能会影响您的浏览，还望能谅解。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">2、如何取消订单？<br />\r\n</span><span style=\"font-size: medium;\">您可以进入&quot;我的笨虎&mdash;我的订单&quot;自行操作订单取消或联系客服处理。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">3、我的积分有什么用途？<br />\r\n</span><span style=\"font-size: medium;\">您可以用积分至笨虎之家&quot;积分商城&quot;免费兑换或积分+现金的形式购买积分优惠商品。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">4、订单已提交成功，什么时候可以发货？<br />\r\n</span><span style=\"font-size: medium;\">订单提交成功后我们会尽快发货，详细进度查询，您可进入&quot;我的笨虎&mdash;我的订单&quot;点击&quot;查看订单&quot;可实时查看订单进度。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">5、订单取消了，还想要，能不能再配送过来？<br />\r\n</span><span style=\"font-size: medium;\">非常抱歉，订单一旦取消将无法恢复，建议您重新下单购买。</span></p>\r\n<p>&nbsp;</p>\r\n<p><span style=\"font-size: medium;\">6，笨虎之家客服工作时间是什么时候？<br />\r\n</span></p>\r\n<ul>\r\n    <li><span style=\"font-size: medium;\">笨虎之家投诉客服QQ：2237209139</span></li>\r\n</ul>\r\n<p><span style=\"font-size: medium;\">服务时间：每日09：00-17：00</span></p>\r\n<p><span style=\"font-size: medium;\">笨虎之家客服QQ：<br />\r\n</span></p>\r\n<ul>\r\n    <li><span style=\"font-size: medium;\">笨笨：2271472670<br />\r\n    </span></li>\r\n    <li><span style=\"font-size: medium;\">虎虎：3072969429<br />\r\n    </span></li>\r\n    <li><span style=\"font-size: medium;\">之之：1793367166<br />\r\n    </span></li>\r\n    <li><span style=\"font-size: medium;\">家家：3073216725<br />\r\n    </span></li>\r\n</ul>\r\n<p><span style=\"font-size: medium;\">服务时间：每日09：00-22：00</span></p>\r\n<p><span style=\"font-size: medium;\">笨虎之家客服电话&nbsp;：