INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('21', '8', '退换货原则', '<p style=\"text-align: left;\"><span style=\"font-size: medium;\"><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8</w:DrawingGridVerticalSpacing><w:View>Normal</w:View><w:Compatibility><w:DontGrowAutofit /><w:UseFELayout /></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--> </span></p>\r\n<p class=\"p0\" style=\"margin-bottom: 7.05pt; margin-top: 7.05pt; text-align: left;\"><span style=\"font-size: medium;\"><strong>退换货原则</strong><span style=\"font-family: &quot;宋体&quot;;\"><br />\r\n</span><span style=\"font-family: &quot;宋体&quot;;\">&nbsp;</span><span style=\"font-family: &quot;宋体&quot;;\">1</span><span style=\"font-family: &quot;宋体&quot;;\">、如发生退货，我们将收回相应的赠品、</span><span style=\"font-family: &quot;宋体&quot;;\">积分</span><span style=\"font-family: &quot;宋体&quot;;\">及其他相关优惠；</span><span style=\"font-family: &quot;宋体&quot;;\"><br />\r\n</span><span style=\"font-family: &quot;宋体&quot;;\">&nbsp;</span><span style=\"font-family: &quot;宋体&quot;;\">2</span><span style=\"font-family: &quot;宋体&quot;;\">、对于需要收取&ldquo;</span><span style=\"font-family: &quot;宋体&quot;;\">运</span><span style=\"font-family: &quot;宋体&quot;;\">费</span><span style=\"font-family: &quot;宋体&quot;;\">&rdquo;的订单</span><span style=\"font-family: &quot;宋体&quot;;\">,由于</span><span style=\"font-family: &quot;宋体&quot;;\">客户自身原因</span><span style=\"font-family: &quot;宋体&quot;;\">发生</span><span style=\"font-family: &quot;宋体&quot;;\">的</span><span style=\"font-family: &quot;宋体&quot;;\">整单退货